mapper.select:
SELECT

FROM ass_asset_used_dtl a

WHERE a.status != #{STATUS_DELETE}
AND return_status=0

    AND a.asset_name = #{assetName}


    AND a.sort_code = #{sortCode}


    AND a.sort_name = #{sortName}


    AND a.asset_code not in

        #{item}



    AND a.user_code = #{user.userCode}

SELECT

FROM ass_asset_used_dtl a

WHERE a.status != #{STATUS_DELETE}
    AND a.used_code = #{usedCode}

SELECT 

FROM ass_asset_used_dtl a

WHERE a.id = #{id}

SELECT 

FROM ass_asset_used_dtl a


	a.status != #{STATUS_DELETE}

		AND a.status = #{status}


		AND a.asset_name = #{assetName}


		AND a.sort_name = #{sortName}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM ass_asset_used_dtl a


	a.status != #{STATUS_DELETE}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE ass_asset_used_dtl SET
	return_status=1
WHERE asset_code = #{assetCode}

UPDATE ass_asset_used_dtl SET 
	update_by = #{updateBy.id},
	update_date = #{updateDate},
